  RotationControl,
  Popup,
  Cluster,
} from 'react-mapbox-gl';
import MapirSource from './components/source.js';
import MarkerComponent from './components/MarkerComponent.js';
import {
  setRTLTextPlugin,
  GeolocateControl,
  AttributionControl,
