  constructor(props) {
    super(props);
    this.setAttribution = this.setAttribution.bind(this);
  }

  componentDidMount() {
    axios({
      method: 'GET',
      url: `https://map.ir/vector/load?x-api-key=${this.props.apiKey}`,
    })
