      .catch(function (err) {});
  }

  setAttribution(map) {
    if (this.props.userLocation) {
      map.addControl(
        new GeolocateControl({
          positionOptions: {
            enableHighAccuracy: false,
            timeout: 5000,
            maximumAge: 10000,
          },
          trackUserLocation: true,
        })
      );
    }
